update poi set descricao = "Foi um elemento principal da missão. Nesse lugar a gente realizava todo tipo de atividade esportiva e cultural. Tem uma cruz em cada angulo e no centro o Santo Patrono: ‘A Divina Criança Jesus’." where id=4;
